function Navigation({ activePage, logo }) {

    const menuOpenIcon = document.getElementById("menuOpenIcon");
    const menuClosedIcon = document.getElementById("menuClosedIcon");
    const mobileMenu = document.getElementById("mobileMenu");
    const menuKalender = document.getElementById("menuKalender");
    const menuHome = document.getElementById("menuHome");
    const menuMannschaften = document.getElementById("menuMannschaft");
    const menuGallery = document.getElementById("menuGallery");


  const [pages, setImageLinks] = useState([])
  //useEffect hook retrieves posts on initial load.
  useEffect(() => {
    async function setNav() {

        if(activePage == "Home"){
            menuHome.classList.add("bg-gray-900");
            menuMannschaften.classList.remove("bg-gray-900");
            menuGallery.classList.remove("bg-gray-900");
            menuKalender.classList.remove("bg-gray-900");
        }else if( activePage == "Mannschaften"){
            menuHome.classList.remove("bg-gray-900");
            menuMannschaften.classList.add("bg-gray-900");
            menuGallery.classList.remove("bg-gray-900");
            menuKalender.classList.remove("bg-gray-900");
        }else if( activePage == "Gallery"){
            menuHome.classList.remove("bg-gray-900");
            menuMannschaften.classList.remove("bg-gray-900");
            menuGallery.classList.add("bg-gray-900");
            menuKalender.classList.remove("bg-gray-900");
        }else if( activePage == "Kalender"){
            menuHome.classList.remove("bg-gray-900");
            menuMannschaften.classList.remove("bg-gray-900");
            menuGallery.classList.remove("bg-gray-900");
            menuKalender.classList.add("bg-gray-900");
        }
    }
    setNav()
  }, [])



    var navigationOpen = false;

    const toggleNavIcon = () => {
        navigationOpen = !navigationOpen;
        if(!navigationOpen){
            menuOpenIcon.classList.add("hidden");
            menuOpenIcon.remove("block");
            menuClosedIcon.classList.add("block");
            menuClosedIcon.classList.remove("hidden"); 
            mobileMenu.classList.add("hidden");
            mobileMenu.classList.remove("block"); 
        }else{
            menuOpenIcon.classList.add("block");
            menuOpenIcon.remove("hidden");
            menuClosedIcon.classList.add("hidden");
            menuClosedIcon.classList.remove("block"); 
            mobileMenu.classList.add("block");
            mobileMenu.classList.remove("hidden"); 
        }
    }

    return (
        <nav className="bg-gray-800">
            <div className="max-w-7xl mx-auto px-2 sm:px-6 lg:px-8">
                <div className="relative flex items-center justify-between h-16">
                    <div className="absolute inset-y-0 left-0 flex items-center sm:hidden">
                        {/*
                            <!-- Mobile menu button-->
                        */}
                        <button onClick={(e)=> { toggleNavIcon(); } } className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white" aria-expanded="false">
                            <span className="sr-only">Open main menu</span>
                            {/*
                                <!-- Icon when menu is closed. -->
                                <!--
                                    Heroicon name: outline/menu

                                    Menu open: "hidden", Menu closed: "block"
                                -->
                            */}
                            <svg id="menuClosedIcon" className="block h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 6h16M4 12h16M4 18h16" />
                            </svg>
                            {/*
                                <!-- Icon when menu is open. -->
                                <!--
                                    Heroicon name: outline/x

                                    Menu open: "block", Menu closed: "hidden"
                                -->
                            */}
                            <svg id="menuOpenIcon" className="hidden h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12" />
                            </svg>
                        </button>
                    </div>
                    <div className="flex-1 flex items-center justify-center sm:items-stretch sm:justify-start">
                        <div className="flex-shrink-0 flex items-center">
                            <img className="block lg:hidden h-8 w-auto" src={logo} alt="Workflow" />
                            <img className="hidden lg:block h-8 w-auto" src={logo} alt="Workflow" />
                        </div>
                        <div className="hidden sm:block sm:ml-6">
                            <div className="flex space-x-4">
                                {/*<!-- Current: "bg-gray-900 text-white", Default: "text-gray-300 hover:bg-gray-700 hover:text-white" -->*/}
                                <a id="menuHome" href="/" className="text-white px-3 py-2 rounded-md text-sm font-medium">Home</a>
                                <a id="menuMannschaft" href="/mannschaften" className="text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium">Mannschaften</a>
                                <a id="menuGallery" href="#" className="text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium">Gallery</a>
                                <a id="menuKalender" href="#" className="text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium">Kalender</a>
                            </div>
                        </div>
                    </div>
                    <div className="absolute inset-y-0 right-0 flex items-center pr-2 sm:static sm:inset-auto sm:ml-6 sm:pr-0">
                        <button className="bg-gray-800 p-1 rounded-full text-gray-400 hover:text-white focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-white">
                            <span className="sr-only">View notifications</span>
                            {/*<!-- Heroicon name: outline/bell -->*/}
                            <svg className="h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
                            </svg>
                        </button>

                        {/*  <!-- Profile dropdown -->*/}
                        <div className="ml-3 relative">
                            <div>
                                <button className="bg-gray-800 flex text-sm rounded-full focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-white" id="user-menu" aria-haspopup="true">
                                    <span className="sr-only">Open user menu</span>
                                    <img className="h-8 w-8 rounded-full" src="https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80" alt="" />
                                </button>
                            </div>

                            {/* 
                                <!--Profile dropdown panel, show/hide based on dropdown state.

                                Entering: "transition ease-out duration-100"
                                From: "transform opacity-0 scale-95"
                                To: "transform opacity-100 scale-100"
                                Leaving: "transition ease-in duration-75"
                                From: "transform opacity-100 scale-100"
                                To: "transform opacity-0 scale-95"
                            -->
                            */}
                            <div className="origin-top-right absolute right-0 mt-2 w-48 rounded-md shadow-lg py-1 bg-white ring-1 ring-black ring-opacity-5" role="menu" aria-orientation="vertical" aria-labelledby="user-menu">
                                <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100" role="menuitem">Your Profile</a>
                                <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100" role="menuitem">Settings</a>
                                <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100" role="menuitem">Sign out</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            {/* 
            <!--
                Mobile menu, toggle classes based on menu state.

                Menu open: "block", Menu closed: "hidden"
            -->
            */}
            <div id="mobileMenu" className="hidden sm:hidden">
                <div className="px-2 pt-2 pb-3 space-y-1">
